      {
         for (prop in params)
            this[prop] = params[prop];

         this.openSideNav = function () {
            // Open the side navigation menu using the Snap library.
            var snapContent = $(".snap-content");
            var snapper = snapContent.data("snap");
            if (snapper == null) {
               snapper = new Snap({ element: snapContent.get(0), disable: 'right' });
               snapContent.data("snap", snapper);
            }

            // Set up all nav links on the side menu to close the menu when clicked.
            $.each($(".my-side-nav a"), function (idx, elem) {
               var navLink = $(this);
               if (!navLink.hasClass("snap-close"))
                  navLink.addClass("snap-close").click(function () { snapper.close() });
            });

            snapper.open("left");
         };
      },
      template: require('raw-loader!my-components/component-my-header-bar.html')
   });

   // Shopping cart.
   ko.components.register('my-cart-icon', {
      viewModel: function (params)
      {
         for (prop in params)
            this[prop] = params[prop];
      },
      template: require('raw-loader!my-components/component-my-cart-icon.html')
   });

   // Menu item.
   ko.components.register('my-menu', {
      viewModel: function (params)
      {
         for (prop in params)
            this[prop] = params[prop];
      },
      template: require('raw-loader!my-components/component-my-menu.html')
   });

   // Menu item.
   ko.components.register('my-menu-item', {
      viewModel: function (params)
      {
         for (prop in params)
            this[prop] = params[prop];
      },
      template: require('raw-loader!my-components/component-my-menu-item.html')
   });

   // Tab panel with slider to indicate tab selection.
   ko.components.register('my-tab-panel', {
      viewModel: function (params)
      {
         this.selected = ko.isObservable(params.selected) ? params.selected : ko.observable(params.selected);
      },
      template: require('raw-loader!my-components/component-my-tab-panel.html')
   });

   // Tab.
   ko.components.register('my-tab', {
      viewModel: function (params)
      {
         var self = this;
         for (prop in params)
            self[prop] = params[prop];

         self.onClick = function (data, event)
         {
            var tabPanel = $(event.target).closest('.tab-panel').get(0);
            ko.contextFor(tabPanel).$data.selected(self.id);
            return true;
         };
      },
      template: require('raw-loader!my-components/component-my-tab.html')
   });

   // Toaster.
   ko.components.register('my-toaster', {
      viewModel: function (params)
      {
         var self = this;
         self.state = ko.observable(null);
         self.timeout = 2000;

         for (prop in params)
            self[prop] = params[prop];

         self.show = function (vm)
         {
            if (self.state() !== null) {
               self.state('show');
               setTimeout(function () { self.state('') }, self.timeout);
            }
            else
               self.state('');
         };
      },
      template: require('raw-loader!my-components/component-my-toaster.html')
   });

